interface DisqusJSConfig {
    shortname: string;
    siteName?: string;
    identifier?: string;
    url: string;
    title?: string;
    api?: string;
    apikey: string | string[];
    nesting?: number;
    nocomment?: string;
    admin?: string;
    adminLabel?: string;
}

declare class DisqusJS {
    private config;
    private container?;
    constructor(config: DisqusJSConfig);
    render(el?: string | Element): void;
    destroy(): void;
}

export { DisqusJSConfig, DisqusJS as default };
